function App() {
  const [policy, setPolicy] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showPolicyRegister, setShowPolicyRegister] = useState(false);
  const [showPolicyImageRegister, setShowPolicyImageRegister] = useState(false);
  const [showSeal, setShowSeal] = useState(false);
  const [showEdit, setShowEdit] = useState(false);
  const dropdownRef = useRef();

  // 드롭다운 외부 클릭 시 닫기
  React.useEffect(() => {
    function handleClick(e) {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    }
    if (showDropdown) document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [showDropdown]);

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<HomeDashboard />} />
          <Route path="/home/todo" element={<TodoDashboard />} />
          <Route path="/policy" element={
            <div className="relative">
              <PolicyDashboard
                onOpenRegister={() => setShowDropdown(v => !v)}
                onOpenDirect={() => setShowSeal(true)}
                policy={policy}
              />
              {/* 드롭다운 */}
              {showDropdown && (
                <div ref={dropdownRef} className="absolute top-16 right-0 z-50">
                  <RegisterDropdown
                    onDirect={() => { setShowDropdown(false); setShowPolicyRegister(true); }}
                    onImage={() => { setShowDropdown(false); setShowPolicyImageRegister(true); }}
                    onClose={() => setShowDropdown(false)}
                  />
                </div>
              )}
              {/* 직접 등록 모달 */}
              {showPolicyRegister && <PolicyRegisterModal onClose={() => setShowPolicyRegister(false)} onSave={setPolicy} />}
              {/* 이미지 등록 모달 */}
              {showPolicyImageRegister && <PolicyImageRegisterModal onClose={() => setShowPolicyImageRegister(false)} onSave={setPolicy} />}
              {/* 직인등록, 수정 모달은 필요시 구현 */}
            </div>
          } />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
