import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Sidebar({ isOpen, setIsOpen }) {
  const [expandedMenu, setExpandedMenu] = useState(null);

  const menuItems = [
    {
      id: 'home',
      title: 'Ìôà',
      icon: 'üè†',
      to: '/',
      submenu: [
        { title: 'Ìï† Ïùº', path: '/home/todo' },
        { title: 'ÌÜµÌï© ÎåÄÏãúÎ≥¥Îìú', path: '/home/dashboard' },
        { title: 'ÏÇ¨ÏóÖÏû• ÎåÄÏãúÎ≥¥Îìú', path: '/home/workplace-dashboard' },
        { title: 'ÏóÖÎ¨¥Í¥ÄÎ¶¨', path: '/home/task-management' },
        { title: 'Ï∫òÎ¶∞Îçî', path: '/home/calendar' },
        { title: 'ÏïàÏ†ÑÎ≥¥Í±¥Í≤ΩÏòÅÏ≤¥Í≥Ñ', path: '/home/safety-management' },
        { title: 'Î∞òÍ∏∞Ï†êÍ≤Ä', path: '/home/semi-annual-check' },
        { title: 'ÏïàÏ†ÑÎ≥¥Í±¥ÍµêÏú°', path: '/home/safety-education' },
        { title: 'TBM', path: '/home/tbm' },
        { title: 'ÏúÑÌóòÏÑ±ÌèâÍ∞Ä', path: '/home/risk-assessment' },
        { title: 'ÏïàÏ†ÑÏ†êÍ≤Ä', path: '/home/safety-inspection' },
        { title: 'ÏúÑÏõêÌöå', path: '/home/committee' },
        { title: 'Ïã†Í≥†/Ï†úÏïà', path: '/home/report-suggestion' },
        { title: 'Î≥¥Ìò∏Íµ¨ Í¥ÄÎ¶¨', path: '/home/ppe-management' },
        { title: 'Î≥¥Ìò∏Íµ¨ ÏßÄÍ∏â', path: '/home/ppe-distribution' },
        { title: 'Î≥¥Ìò∏Íµ¨ Ïã†Ï≤≠', path: '/home/ppe-request' },
        { title: 'ÏúÑÌóòÏûëÏóÖ', path: '/home/hazardous-work' },
        { title: 'ÏÇ∞ÏóÖÏû¨Ìï¥ Ï°∞ÏÇ¨', path: '/home/accident-investigation' },
        { title: 'Í∏∞Í≥Ñ/Í∏∞Íµ¨', path: '/home/machinery' }
      ]
    },
    {
      id: 'contract',
      title: 'ÎèÑÍ∏âÍ¥ÄÎ¶¨',
      icon: 'üìã',
      submenu: [
        { title: 'ÏàòÍ∏âÏóÖÏ≤¥ Í¥ÄÎ¶¨', path: '/contract/subcontractor' },
        { title: 'ÏïàÏ†ÑÎ≥¥Í±¥ÏàòÏ§ÄÌèâÍ∞Ä', path: '/contract/safety-evaluation' },
        { title: 'ÎèÑÍ∏â ÏïàÏ†ÑÏ†êÍ≤Ä', path: '/contract/safety-inspection' }
      ]
    },
    {
      id: 'health',
      title: 'Î≥¥Í±¥Í¥ÄÎ¶¨',
      icon: 'üè•',
      submenu: [
        { title: 'ÌôîÌïôÎ¨ºÏßà Í¥ÄÎ¶¨', path: '/health/chemical' },
        { title: 'Í±¥Í∞ïÏßÄÎã® Í¥ÄÎ¶¨', path: '/health/health-group' },
        { title: 'ÏûëÏóÖÌôòÍ≤ΩÏ∏°Ï†ï', path: '/health/work-environment' },
        { title: 'ÏßÅÎ¨¥Ïä§Ìä∏Î†àÏä§ Í¥ÄÎ¶¨', path: '/health/job-stress' },
        { title: 'Í∑ºÍ≥®Í≤©Í≥Ñ Ïú†Ìï¥ÏöîÏù∏ Ï°∞ÏÇ¨', path: '/health/musculoskeletal-hazard' },
        { title: 'Í∑ºÍ≥®Í≤©Í≥Ñ Ï¶ùÏÉÅ Ï°∞ÏÇ¨', path: '/health/musculoskeletal-symptom' }
      ]
    },
    {
      id: 'ai',
      title: 'AI Î∂ÑÏÑù',
      icon: 'ü§ñ',
      submenu: [
        { title: 'ÏúÑÌóòÏöîÏù∏ ÌååÏïÖ Î∞è Í∞úÏÑ†', path: '/ai/risk-factors' },
        { title: 'AI ÏúÑÌóòÏÑ±ÌèâÍ∞Ä', path: '/ai/risk-assessment' }
      ]
    },
    {
      id: 'admin',
      title: 'Í¥ÄÎ¶¨Ïûê',
      icon: '‚öôÔ∏è',
      submenu: [
        { title: 'ÏÇ¨ÏóÖÏû• Í¥ÄÎ¶¨', path: '/admin/workplace' },
        { title: 'Í≥ÑÏ†ï Í¥ÄÎ¶¨Ïûê Î≥ÄÍ≤Ω', path: '/admin/account-manager' }
      ]
    }
  ];

  const bottomMenuItems = [
    { title: 'ÏÑ§Ï†ï', icon: '‚öôÔ∏è', path: '/settings' },
    { title: 'Í∞úÏù∏ÌîÑÎ°úÌïÑ', icon: 'üë§', path: '/profile' },
    { title: 'Í≤∞Ïû¨Ìï®', icon: 'üìù', path: '/approval' },
    { title: 'Ïù¥Ïö©Í∞ÄÏù¥Îìú', icon: 'üìö', path: '/guide' },
    { title: 'Î°úÍ∑∏ÏïÑÏõÉ', icon: 'üö™', path: '/logout' }
  ];

  const toggleSubmenu = (menuId) => {
    setExpandedMenu(expandedMenu === menuId ? null : menuId);
  };

  return (
    <div className={`bg-blue-800 text-white w-64 space-y-6 py-7 px-2 absolute inset-y-0 left-0 transform ${isOpen ? 'translate-x-0' : '-translate-x-full'} transition duration-200 ease-in-out md:relative md:translate-x-0 flex flex-col h-full`}>
      {/* Î°úÍ≥† */}
      <div className="flex items-center space-x-2 px-4">
        <span className="text-2xl font-extrabold">ÏïàÏ†ÑÎ≥¥Í±¥Í¥ÄÎ¶¨</span>
      </div>

      {/* Î©îÏù∏ Î©îÎâ¥ */}
      <nav className="flex-1 overflow-y-auto">
        {menuItems.map((item) => (
          <div key={item.id}>
            {item.to ? (
              <Link
                to={item.to}
                className="w-full flex items-center space-x-2 px-4 py-2.5 hover:bg-blue-700 rounded transition duration-200"
                onClick={() => toggleSubmenu(item.id)}
              >
                <span className="text-xl">{item.icon}</span>
                <span>{item.title}</span>
                <span className="ml-auto">{expandedMenu === item.id ? '‚ñº' : '‚ñ∂'}</span>
              </Link>
            ) : (
              <button
                onClick={() => toggleSubmenu(item.id)}
                className="w-full flex items-center space-x-2 px-4 py-2.5 hover:bg-blue-700 rounded transition duration-200"
              >
                <span className="text-xl">{item.icon}</span>
                <span>{item.title}</span>
                <span className="ml-auto">{expandedMenu === item.id ? '‚ñº' : '‚ñ∂'}</span>
              </button>
            )}
            {expandedMenu === item.id && (
              <div className="pl-12 py-2 space-y-1">
                {item.submenu.map((subItem, index) => (
                  <Link
                    key={index}
                    to={subItem.path}
                    className="block py-2 px-4 hover:bg-blue-700 rounded transition duration-200"
                  >
                    {subItem.title}
                  </Link>
                ))}
              </div>
            )}
          </div>
        ))}
      </nav>

      {/* ÌïòÎã® Î©îÎâ¥ */}
      <div className="border-t border-blue-700 pt-4">
        {bottomMenuItems.map((item, index) => (
          <Link
            key={index}
            to={item.path}
            className="flex items-center space-x-2 px-4 py-2.5 hover:bg-blue-700 rounded transition duration-200"
          >
            <span className="text-xl">{item.icon}</span>
            <span>{item.title}</span>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default Sidebar;
