import React, { useState, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Layout from './components/Layout';
import HomeDashboard from './components/HomeDashboard';
import TodoDashboard from './components/TodoDashboard';
import PolicyDashboard from './components/PolicyDashboard';

// Î™®Îã¨ Ïª¥Ìè¨ÎÑåÌä∏Îäî ÏùºÎã® Îπà Ïª¥Ìè¨ÎÑåÌä∏Î°ú ÏÉùÏÑ±Ìï¥Îë°ÎãàÎã§.
function RegisterDropdown({ onDirect, onImage, onClose, anchorRef }) {
  return (
    <div className="absolute right-0 mt-2 w-40 bg-white rounded-md shadow-lg py-1 z-50 border" style={{minWidth: '120px'}}>
      <button onClick={onDirect} className="block w-full text-left px-4 py-2 hover:bg-gray-100">ÏßÅÏ†ë Îì±Î°ù</button>
      <button onClick={onImage} className="block w-full text-left px-4 py-2 hover:bg-gray-100">Ïù¥ÎØ∏ÏßÄ Îì±Î°ù</button>
    </div>
  );
}

function PolicyRegisterModal({ onClose, onSave }) {
  const [date, setDate] = useState('');
  const [head, setHead] = useState('');
  const [bodies, setBodies] = useState(['']);
  const [company, setCompany] = useState('');
  const [manager, setManager] = useState('');

  const handleBodyChange = (idx, value) => {
    setBodies(bodies.map((b, i) => (i === idx ? value : b)));
  };
  const addBody = () => setBodies([...bodies, '']);
  const removeBody = (idx) => setBodies(bodies.filter((_, i) => i !== idx));

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ date, head, bodies, company, manager });
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50 overflow-auto">
      <div className="bg-white rounded-xl p-8 max-w-2xl w-full relative">
        <button onClick={onClose} className="absolute top-4 right-4 text-2xl text-gray-400 hover:text-gray-600">√ó</button>
        <h2 className="text-2xl font-bold text-center mb-6">Í≤ΩÏòÅÎ∞©Ïπ® Îì±Î°ù</h2>
        <form className="space-y-6" onSubmit={handleSubmit}>
          <div>
            <label className="block mb-1 font-semibold">Ï†ú/Í∞úÏ†ïÏùº*</label>
            <input type="date" className="w-full border rounded px-3 py-2" value={date} onChange={e => setDate(e.target.value)} />
          </div>
          <div>
            <label className="block mb-1 font-semibold">Î®∏Î¶¨Îßê*</label>
            <input className="w-full border rounded px-3 py-2" value={head} onChange={e => setHead(e.target.value)} placeholder="Î®∏Î¶¨ÎßêÏùÑ ÏûÖÎ†•ÌïòÏÑ∏Ïöî." />
          </div>
          {bodies.map((body, idx) => (
            <div key={idx} className="flex items-center space-x-2">
              <input className="w-full border rounded px-3 py-2" value={body} onChange={e => handleBodyChange(idx, e.target.value)} placeholder={`Î≥∏Î¨∏${idx+1}*`} />
              {bodies.length > 1 && (
                <button type="button" onClick={() => removeBody(idx)} className="text-red-500 text-xl">üóëÔ∏è</button>
              )}
            </div>
          ))}
          <button type="button" onClick={addBody} className="bg-blue-100 text-blue-700 px-4 py-2 rounded">Î≥∏Î¨∏ Ï∂îÍ∞Ä</button>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <label className="block mb-1 font-semibold">ÏÇ¨ÏóÖÏû•Î™Ö</label>
              <input className="w-full border rounded px-3 py-2" value={company} onChange={e => setCompany(e.target.value)} placeholder="ÏÇ¨ÏóÖÏû•Î™Ö" />
            </div>
            <div>
              <label className="block mb-1 font-semibold">Í≤ΩÏòÅÏ±ÖÏûÑÏûê</label>
              <input className="w-full border rounded px-3 py-2" value={manager} onChange={e => setManager(e.target.value)} placeholder="Í≤ΩÏòÅÏ±ÖÏûÑÏûê" />
            </div>
          </div>
          <div className="flex justify-end mt-8">
            <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded flex items-center"><span className="mr-1">üíæ</span> Ï†ÄÏû•</button>
          </div>
        </form>
      </div>
    </div>
  );
}

function SealModal({ onClose }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl p-8 max-w-2xl w-full relative">
        <button onClick={onClose} className="absolute top-4 right-4 text-2xl text-gray-400 hover:text-gray-600">√ó</button>
        <h2 className="text-2xl font-bold text-center mb-6">ÏÇ¨ÏóÖÏû•</h2>
        <form className="space-y-8">
          {/* ÏÇ¨ÏóÖÏû• Ï†ïÎ≥¥ */}
          <div>
            <div className="font-semibold mb-2">ÏÇ¨ÏóÖÏû• Ï†ïÎ≥¥</div>
            <div className="flex items-center space-x-6 mb-4">
              <label className="flex items-center space-x-2">
                <input type="radio" name="type" defaultChecked className="accent-orange-400" />
                <span className="text-orange-500 font-semibold">ÏÇ¨ÏóÖÏû•</span>
              </label>
              <label className="flex items-center space-x-2">
                <input type="radio" name="type" className="accent-orange-400" />
                <span>Í±¥ÏÑ§ÌòÑÏû•</span>
              </label>
            </div>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm mb-1">ÏÇ¨ÏóÖÏû•Î™Ö *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="ÏÇ¨ÏóÖÏû•Î™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
              <div>
                <label className="block text-sm mb-1">ÏÇ¨ÏóÖÏûêÎì±Î°ùÎ≤àÌò∏ *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="ÏÇ¨ÏóÖÏûêÎì±Î°ùÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm mb-1">Ï£ºÏÜå *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="Í≤ΩÎÇ® ÏßÑÏ£ºÏãú Ï¥âÏÑùÎ°ú158Î≤àÍ∏∏ 8" />
                <input className="w-full border rounded px-3 py-2 mt-2" placeholder="ÏÉÅÏÑ∏Ï£ºÏÜåÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
              <div>
                <label className="block text-sm mb-1">ÏÉÅÏãúÍ∑ºÎ°úÏûê Ïàò *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="ÏÉÅÏãúÍ∑ºÎ°úÏûê ÏàòÎ•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
            </div>
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <label className="block text-sm mb-1">ÏóÖÏ¢Ö *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="ÏóÖÏ¢ÖÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî." />
              </div>
              <div className="flex items-end space-x-2">
                <button type="button" className="bg-blue-100 text-blue-700 px-4 py-2 rounded flex items-center"><span className="mr-1">‚¨Ü</span> ÏÇ¨ÏóÖÏûêÎì±Î°ùÏ¶ù</button>
                <button type="button" className="bg-blue-100 text-blue-700 px-4 py-2 rounded flex items-center"><span className="mr-1">‚¨Ü</span> ÏßÅÏù∏</button>
              </div>
            </div>
          </div>
          {/* Îã¥ÎãπÏûê Ï†ïÎ≥¥ */}
          <div>
            <div className="font-semibold mb-2">Îã¥ÎãπÏûê Ï†ïÎ≥¥</div>
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm mb-1">ÏÑ±Î™Ö *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="ÏÑ±Î™ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
              <div>
                <label className="block text-sm mb-1">ÏßÅÏ±Ö</label>
                <input className="w-full border rounded px-3 py-2" placeholder="ÏßÅÏ±ÖÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
              <div>
                <label className="block text-sm mb-1">Ï†ÑÌôîÎ≤àÌò∏ *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="Ï†ÑÌôîÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
              <div>
                <label className="block text-sm mb-1">Ïù¥Î©îÏùº *</label>
                <input className="w-full border rounded px-3 py-2" placeholder="Ïù¥Î©îÏùºÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî." />
              </div>
            </div>
          </div>
          <div className="flex justify-end mt-8">
            <button type="submit" className="bg-blue-500 text-white px-6 py-2 rounded flex items-center"><span className="mr-1">üíæ</span> Ï†ÄÏû•</button>
          </div>
        </form>
      </div>
    </div>
  );
}

function PolicyEditModal({ onClose }) {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-30 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-8 min-w-[400px] max-w-lg w-full">
        <h2 className="text-xl font-bold mb-4">Í≤ΩÏòÅÎ∞©Ïπ® ÏàòÏ†ï</h2>
        {/* Ïã§Ï†ú ÏûÖÎ†• ÌèºÏùÄ Ï∂îÌõÑ Íµ¨ÌòÑ, ÏùºÎã® ÎπàÏπ∏ */}
        <div className="space-y-2 mb-4">
          <div className="bg-gray-100 h-10 rounded" />
          <div className="bg-gray-100 h-10 rounded" />
          <div className="bg-gray-100 h-10 rounded" />
        </div>
        <button onClick={onClose} className="mt-6 text-gray-500">Îã´Í∏∞</button>
      </div>
    </div>
  );
}

function App() {
  const [policy, setPolicy] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [showPolicyRegister, setShowPolicyRegister] = useState(false);
  const [showSeal, setShowSeal] = useState(false);
  const [showEdit, setShowEdit] = useState(false);
  const dropdownRef = useRef();

  // ÎìúÎ°≠Îã§Ïö¥ Ïô∏Î∂Ä ÌÅ¥Î¶≠ Ïãú Îã´Í∏∞
  React.useEffect(() => {
    function handleClick(e) {
      if (dropdownRef.current && !dropdownRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    }
    if (showDropdown) document.addEventListener('mousedown', handleClick);
    return () => document.removeEventListener('mousedown', handleClick);
  }, [showDropdown]);

  return (
    <Router>
      <Layout>
        <Routes>
          <Route path="/" element={<HomeDashboard />} />
          <Route path="/home/todo" element={<TodoDashboard />} />
          <Route path="/policy" element={
            <div className="relative">
              <PolicyDashboard
                onOpenRegister={() => setShowDropdown(v => !v)}
                onOpenDirect={() => setShowSeal(true)}
                policy={policy}
              />
              {/* ÎìúÎ°≠Îã§Ïö¥ */}
              {showDropdown && (
                <div ref={dropdownRef} className="absolute top-16 right-0 z-50">
                  <RegisterDropdown
                    onDirect={() => { setShowDropdown(false); setShowPolicyRegister(true); }}
                    onImage={() => { setShowDropdown(false); alert('Ïù¥ÎØ∏ÏßÄ Îì±Î°ùÏùÄ Ï§ÄÎπÑ Ï§ëÏûÖÎãàÎã§.'); }}
                    onClose={() => setShowDropdown(false)}
                  />
                </div>
              )}
              {/* ÏßÅÏ†ë Îì±Î°ù Î™®Îã¨ */}
              {showPolicyRegister && <PolicyRegisterModal onClose={() => setShowPolicyRegister(false)} onSave={setPolicy} />}
              {/* ÏßÅÏù∏Îì±Î°ù, ÏàòÏ†ï Î™®Îã¨ÏùÄ ÌïÑÏöîÏãú Íµ¨ÌòÑ */}
            </div>
          } />
        </Routes>
      </Layout>
    </Router>
  );
}

export default App;
