import React, { useState } from 'react';

function Header() {
  const [showNotifications, setShowNotifications] = useState(false);
  const [showUserMenu, setShowUserMenu] = useState(false);

  // ÏûÑÏãú ÏÇ¨Ïö©Ïûê Îç∞Ïù¥ÌÑ∞
  const user = {
    name: 'ÌôçÍ∏∏Îèô',
    role: 'Í¥ÄÎ¶¨Ïûê',
    avatar: 'üë§'
  };

  return (
    <header className="bg-white shadow-sm">
      <div className="max-w-full mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <h1 className="text-xl font-semibold text-gray-800">ÎåÄÏãúÎ≥¥Îìú</h1>
          </div>

          <div className="flex items-center space-x-4">
            {/* ÏïåÎ¶º ÏïÑÏù¥ÏΩò */}
            <div className="relative">
              <button
                onClick={() => setShowNotifications(!showNotifications)}
                className="p-2 rounded-full text-gray-600 hover:bg-gray-100 focus:outline-none"
              >
                <span className="text-xl">üîî</span>
                <span className="absolute top-0 right-0 block h-2 w-2 rounded-full bg-red-500"></span>
              </button>

              {/* ÏïåÎ¶º ÎìúÎ°≠Îã§Ïö¥ */}
              {showNotifications && (
                <div className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg py-1 z-10">
                  <div className="px-4 py-2 border-b">
                    <h3 className="text-sm font-semibold">ÏïåÎ¶º</h3>
                  </div>
                  <div className="max-h-60 overflow-y-auto">
                    <a href="#" className="block px-4 py-2 hover:bg-gray-100">
                      <p className="text-sm text-gray-700">ÏÉàÎ°úÏö¥ ÏïàÏ†ÑÎ≥¥Í≥†ÏÑúÍ∞Ä Îì±Î°ùÎêòÏóàÏäµÎãàÎã§.</p>
                      <p className="text-xs text-gray-500">5Î∂Ñ Ï†Ñ</p>
                    </a>
                    {/* Ï∂îÍ∞Ä ÏïåÎ¶º Ìï≠Î™©Îì§ */}
                  </div>
                </div>
              )}
            </div>

            {/* ÏÇ¨Ïö©Ïûê Î©îÎâ¥ */}
            <div className="relative">
              <button
                onClick={() => setShowUserMenu(!showUserMenu)}
                className="flex items-center space-x-3 focus:outline-none"
              >
                <span className="text-2xl">{user.avatar}</span>
                <div className="text-left">
                  <p className="text-sm font-medium text-gray-700">{user.name}</p>
                  <p className="text-xs text-gray-500">{user.role}</p>
                </div>
              </button>

              {/* ÏÇ¨Ïö©Ïûê Î©îÎâ¥ ÎìúÎ°≠Îã§Ïö¥ */}
              {showUserMenu && (
                <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-10">
                  <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                    ÌîÑÎ°úÌïÑ
                  </a>
                  <a href="#" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">
                    ÏÑ§Ï†ï
                  </a>
                  <div className="border-t border-gray-100"></div>
                  <a href="#" className="block px-4 py-2 text-sm text-red-600 hover:bg-gray-100">
                    Î°úÍ∑∏ÏïÑÏõÉ
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}

export default Header;
