import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Sidebar({ isOpen, setIsOpen }) {
  const [expandedMenu, setExpandedMenu] = useState(null);

  const menuItems = [
    {
      id: 'home',
      title: 'í™ˆ',
      icon: 'ðŸ ',
      path: '/'
    },
    {
      id: 'contract',
      title: 'ë„ê¸‰ê´€ë¦¬',
      icon: 'ðŸ“‹',
      submenu: [
        { title: 'ë„ê¸‰ì—…ì²´ ëª©ë¡', path: '/contract/list' },
        { title: 'ê³„ì•½ ê´€ë¦¬', path: '/contract/manage' },
        { title: 'í‰ê°€ ê´€ë¦¬', path: '/contract/evaluation' }
      ]
    },
    {
      id: 'health',
      title: 'ë³´ê±´ê´€ë¦¬',
      icon: 'ðŸ¥',
      submenu: [
        { title: 'ê±´ê°•ê²€ì§„ ê´€ë¦¬', path: '/health/checkup' },
        { title: 'ìž‘ì—…í™˜ê²½ ì¸¡ì •', path: '/health/environment' },
        { title: 'ë³´ê±´êµìœ¡', path: '/health/education' }
      ]
    },
    {
      id: 'ai',
      title: 'AI ë¶„ì„',
      icon: 'ðŸ¤–',
      submenu: [
        { title: 'ìœ„í—˜ë„ ë¶„ì„', path: '/ai/risk' },
        { title: 'ì˜ˆì¸¡ ëª¨ë¸', path: '/ai/prediction' },
        { title: 'ë³´ê³ ì„œ ìƒì„±', path: '/ai/report' }
      ]
    },
    {
      id: 'admin',
      title: 'ê´€ë¦¬ìž',
      icon: 'âš™ï¸',
      submenu: [
        { title: 'ì‚¬ìš©ìž ê´€ë¦¬', path: '/admin/users' },
        { title: 'ì‹œìŠ¤í…œ ì„¤ì •', path: '/admin/settings' },
        { title: 'ë¡œê·¸ ê´€ë¦¬', path: '/admin/logs' }
      ]
    }
  ];

  const toggleSubmenu = (menuId) => {
    setExpandedMenu(expandedMenu === menuId ? null : menuId);
  };

  return (
    <div className={`bg-blue-800 text-white w-64 space-y-6 py-7 px-2 absolute inset-y-0 left-0 transform ${isOpen ? 'translate-x-0' : '-translate-x-full'} transition duration-200 ease-in-out md:relative md:translate-x-0`}>
      {/* ë¡œê³  */}
      <div className="flex items-center space-x-2 px-4">
        <span className="text-2xl font-extrabold">ì•ˆì „ë³´ê±´ê´€ë¦¬</span>
      </div>

      {/* ë©”ë‰´ */}
      <nav>
        {menuItems.map((item) => (
          <div key={item.id}>
            {item.submenu ? (
              <div>
                <button
                  onClick={() => toggleSubmenu(item.id)}
                  className="w-full flex items-center space-x-2 px-4 py-2.5 hover:bg-blue-700 rounded transition duration-200"
                >
                  <span className="text-xl">{item.icon}</span>
                  <span>{item.title}</span>
                  <span className="ml-auto">
                    {expandedMenu === item.id ? 'â–¼' : 'â–¶'}
                  </span>
                </button>
                {expandedMenu === item.id && (
                  <div className="pl-12 py-2 space-y-1">
                    {item.submenu.map((subItem, index) => (
                      <Link
                        key={index}
                        to={subItem.path}
                        className="block py-2 px-4 hover:bg-blue-700 rounded transition duration-200"
                      >
                        {subItem.title}
                      </Link>
                    ))}
                  </div>
                )}
              </div>
            ) : (
              <Link
                to={item.path}
                className="flex items-center space-x-2 px-4 py-2.5 hover:bg-blue-700 rounded transition duration-200"
              >
                <span className="text-xl">{item.icon}</span>
                <span>{item.title}</span>
              </Link>
            )}
          </div>
        ))}
      </nav>
    </div>
  );
}

export default Sidebar;
